import React, { Component } from 'react';
import axios from 'axios'


class homeForm extends Component {

    constructor(props) {
        super(props);

        this.state={
            img: "http://zinogene.com/wp-content/uploads/2020/04/Social-Media-Video-Specs-Feature-Image.png"
        };

        this.handleSubmit = this.handleSubmit.bind(this);
      }
    imageHandler = (e) =>{
        // const reader = new FileReader();
        this.setState({
            img: e.target.files[0]
        })
        // reader.onload=()=>{
        //     if(reader.readyState===2){
        //         this.setState({img:reader.result})
        //         console.log(e.target.files[0])

        //     }
        // }

        // reader.readAsDataURL(e.target.files[0])


    };
    handleSubmit(e){
        e.preventDefault();

        // axios({
        //     method: 'post',
        //     url: 'http://127.0.0.1:8000/api_images/',
        //     data: {
        //       pic: this.state.img,
        //       name: "mahdi"
        //     }
        //   }).then(res => console.log(res))
        //   .catch(err => console.log(err));

        let form_data = new FormData();
        form_data.append('pic', this.state.img, this.state.img.name);
        form_data.append('name', "mahdi");
        let url = 'http://localhost:8000/api_images/';
        axios.post(url, form_data, {
          headers: {
            'content-type': 'multipart/form-data'
          }
        })
        .then(res => {
          console.log(res.data);
        })
        .catch(err => console.log(err))

    };

    render() {
        const { img } = this.state
        this.props.isAuthed = true

        return (
        <form className="homefields" onSubmit={this.handleSubmit}>

            {
                this.props.isAuthed ?

                <h> isAuthenticated True </h>
                :
                <h> isAuthenticated False {this.props.isAuthed} s </h>
            }



            <div className="custom-file">
                <div className="img-holder">
                    <img src={img}  alt="" className="img"></img>
                </div>
                    <label className="fileCustom__Input">
                        <input type="file" id="customFile" accept="image/*" onChange={this.imageHandler}/>
                    </label>

                <button  className="FormField__Button mr-20" > Analysis </button>

            </div>
        </form>

        );
    }
}

export default homeForm;
