// const { Component } = require("react")

import React, { Component } from "react";
import { NavLink } from "react-router-dom";

const emailRegex = RegExp(/^[a-zA-Z0-9]+@(?:[a-zA-Z0-9]+\.)+[A-Za-z]+$/);

const formValid = formErrors => {
    let valid = true

    Object.values(formErrors).forEach(val => { val.length > 0 && (valid = false) });
    return valid;

};

class signUpForm extends Component {
    constructor(props) {
        super(props);

        this.state = {
            fullName: "",
            email: "",
            password: "",
            rePassword: "",
            isDoctor: false,
            doctorID: "",
            formErrors: {
                fullName: "",
                email: "",
                password: "",
                rePassword: "",
            }
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(e) {
        e.preventDefault();
        if (formValid(this.state.formErrors)) {
            console.log('submited')

        }
        else {
            console.log("Error")
        }
    }

    handleChange(e) {
        let target = e.target;
        let value = target.type === "checkbox" ? target.checked : target.value;
        let name = target.name;

        let formErrors = this.state.formErrors;

        switch (name) {
            case 'fullName':
                formErrors.fullName = value.length < 3 ? 'Minimum 3 characters required' : '';
                break;
            case 'email':
                formErrors.email = emailRegex.test(value) ? '' : 'Invalid email address';
                break;
            case 'password':
                formErrors.password = value.length < 6 ? 'Minimum 6 characters required' : '';
                break;
            case 'rePassword':
                formErrors.rePassword = this.state.password !=val < 6 ? 'Minimym 3 characters required' : '';
                break;
            // case 'isDocter':
            //     formErrors.fullName = value.length < 3 ? 'Minimym 3 characters required' : '';
            //     break;
            case 'doctorID':
                formErrors.fullName = value.length < 3 ? 'Minimym 3 characters required' : '';
                break;
            default:
        }

        this.setState(
            {
                formErrors,
                [name]: value,
            }
            ,()=>console.log(this.state)
        );
    }
    render() {
        return (

            <div className="FormCenter" >
                <div className="PageSwitcher">
                    <NavLink to="/sign-in" activeClassName="PageSwitcher__Item--Active" className="PageSwitcher__Item ">Sign In</NavLink>
                    <NavLink to="/sign-up" activeClassName="PageSwitcher__Item--Active" className="PageSwitcher__Item ">Sign Up</NavLink>
                </div>
                <div className="FormTitle">
                    <NavLink to="/sign-in" activeClassName="FormTitle__Link--Active"
                        className="FormTitle__Link">Sign In</NavLink> or <NavLink exact to="/sign-up"
                            activeClassName="FormTitle__Link--Active" className="FormTitle__Link">Sign Up</NavLink>
                </div>
                <form className="FormFields"
                    onSubmit={this.handleSubmit} >

                    <div className="FormFields" >
                        <label className="FormField__Label"
                            htmlFor="fullName" > full Name </label>
                        <input type="text"
                            id="fullName"
                            className="FormField__Input"
                            placeholder="Enter your full name"
                            name="fullName"
                            onChange={this.handleChange}
                            value={this.state.fullName} />

                    </div>
                    <div className="FormFields" >

                        <label className="FormField__Label"
                            htmlFor="email" > Email </label>
                        <input type="text"
                            id="email"
                            className="FormField__Input"
                            placeholder="Enter your Email address"
                            name="email"
                            onChange={this.handleChange}
                            value={this.state.email} />

                    </div>
                    <div className="FormFields" >

                        <label className="FormField__Label"
                            htmlFor="password" > Password </label>
                        <input type="password"
                            id="password"
                            className="FormField__Input"
                            placeholder="Enter your password"
                            name="password"
                            onChange={this.handleChange}
                            value={this.state.password} />

                    </div>
                    <div className="FormFields" >
                        <label className="FormField__Label"
                            htmlFor="repeat password" > Repeat Password </label>
                        <input type="password"
                            id="rePassword"
                            className="FormField__Input"
                            placeholder="Repeat your password"
                            name="rePassword"
                            onChange = {this.handleChange}
                            value={this.state.rePassword}
                            />

                    </div>


                    <label className="FormField__CheckboxLabel" >

                        <input className="FormField__Checkbox"
                            type="checkbox"
                            id="isDoctor"
                            name="isDoctor"
                            onChange={this.handleChange}
                            value={this.state.isDoctor}
                             /> Are you a doctor ?</label>

                    <div className="FormFields" >

                        <input type="text"
                            id="doctorID"
                            className="FormField__Input"
                            placeholder="Enter your doctorID"
                            name="doctorID"
                            hidden={true}
                            onChange={this.handleChange}
                            value={this.state.doctorID} />
                    </div >


                    <div className="FromFields" >

                        <button className="FormField__Button mr-20" > Sign Up </button>
                    </div >
                </form>
            </div >
        );

    }
}

export default signUpForm;
