import * as actionTypes from "./actionTypes"
import axios from 'axios'

export const authStart = () => {
    return{
        type: actionTypes.AUTH_START
    }
}


export const authSuccess = token => {
    return{
        type: actionTypes.AUTH_SUCCESS,
        token: token
    }
}


export const authFail = error => {
    return{
        type: actionTypes.AUTH_FAIL,
        error: error
    }
}


export const logout = () => {
    localStorage.removeItem('user');
    localStorage.removeItem('expirationDate');
    return{
        type: actionTypes.AUTH_LOGOUT
    }

}



export const checkAuthTimeout = expirationTime => {
    return dispath => {
        setTimeout(() => {
            dispath(logout())
        }, expirationTime * 1000)
    }
}




export const authLogin = (uname, pass) => {
    return dispatch => {
        dispatch(authStart());
        axios.post('http://localhost:8000/api_user/login/', {
            username: uname, 
            password: pass
        })
        .then(res => {
            const token = res.data.token;
            const expirationDate = new Date(new Date().getTime() + 3600 * 1000);
            localStorage.setItem('token', token);
            localStorage.setItem('expirationDate', expirationDate);
            dispatch(authSuccess(token));
            dispatch(checkAuthTimeout(3600));
        })
        .catch(err => {
            console.log(err.response)
            dispatch(authFail(err))
        })

    }
}


export const authSignup = (email, _password_, _FullName_, _phoneNumber_, _is_doctor_, _doctor_id_) => {
    return dispath => {
        dispath(authStart);
        axios.post('http://localhost:8000/api-user/register/', {
            username: email,
            phone_number: _phoneNumber_,
            password: _password_,
            ful_name: _FullName_,
            is_doctor: "true",
            doctor_id: _doctor_id_
        })
        .then(res => {
            // const token = res.data.key;
            const expirationDate = new Date(new Date().getTime() + 3600 * 1000);
            // localStorage.setItem('token', token);
            localStorage.setItem('expirationDate', expirationDate);
            // dispath(authSuccess(token));
            dispath(checkAuthTimeout(3600))
        })
        .catch(err=> {
            dispath(authFail(err))
            console.log(err.response)
        })

    }
}


export const authCheckState = () => {
    return dispatch => {
        const token = localStorage.getItem('token');
        if (token === undefined) {
            dispatch(logout());
        } else {
            const expirationDate = new Date(localStorage.getItem('expirationDate'));
            if ( expirationDate <= new Date() ) {
                dispatch(logout());
            } else {
                dispatch(authSuccess(token));
                dispatch(checkAuthTimeout( (expirationDate.getTime() - new Date().getTime()) / 1000) );
            }
        }
    }
}
